sys_script_include.script:
var Marvel_API = Class.create();
Marvel_API.prototype = {
    initialize: function() {},

    getCharacters: function(hero) {
        var pub_key = gs.getProperty('x_543178_marvel_0.PUBLIC_KEY');
        var private_key = gs.getProperty('x_543178_marvel_0.PRIVATE_KEY');
        var ts = Date.now();
        var hashString = ts + private_key + pub_key;
        var digest = new GlideDigest();
        var hash = digest.getMD5Hex(hashString).toLowerCase();
        gs.info("Marvel App-hash " + hash);
        try {
            var r = new sn_ws.RESTMessageV2('Marvel', 'Characters');
            r.setQueryParameter('hash', hash);
            r.setQueryParameter('name', hero);
            r.setQueryParameter('apikey', pub_key);
            r.setQueryParameter('ts', ts);
            var response = r.execute();
            var responseBody = response.getBody();
            var httpStatus = response.getStatusCode();
            gs.info("Marvel App res " + responseBody);
        } catch (ex) {
            var message = ex.message;
            gs.info("Marvel App " + ex);
        }
    },

    type: 'Marvel_API'
};
